import React , { useState} from 'react'

export default function TextForm(props) {
  const handleUPClick= () => {
    console.log("Uppercase was clicked"+text);
    const newt = text.toUpperCase();
    setText(newt);

  }
  const handleOnChange= (event) => {
    console.log("on change");
    setText(event.target.value)
  }
  const speak = () => {
    let msg = new SpeechSynthesisUtterance();
    msg.text = text;
    window.speechSynthesis.speak(msg);
  }
  const handleSnakecase = () => {
    console.log("on handleSnakecase");
    let newtext=text.split(" ");
    let res=newtext.join("_");
    setText(res);
  }
  const handleAltercase= () => {
    console.log("Uppercase was clicked"+text);
    let res="";
    for(let i=0;i<text.length;i++){
        if(text[i]>="a" && text[i]<="z"){
          res+=String.fromCharCode(text.charCodeAt(i) - 32)
        }
        else if(text[i]>="A" && text[i]<="Z"){
          res+=String.fromCharCode(text.charCodeAt(i) + 32)
        }
        else{
          res+=" ";
        }
    }
    setText(res);

  }




  const [text, setText] = useState("");
  return (
    <>
    <div className="container">
      <h2>{props.title}</h2>
        <div className="mb-3">

        <textarea className="form-control" value={text} onChange={handleOnChange} id="exampleFormControlTextarea1" rows="6"></textarea>
        </div>
        <button className="btn btn-primary mx-2" onClick={handleUPClick}>Convert to UpperCase</button>
        <button className="btn btn-primary mx-2" onClick={handleSnakecase}>Convert to SnakeCase</button>
        <button className="btn btn-primary mx-2" onClick={handleAltercase}>Alter Case</button>
        <button type="submit" onClick={speak} className="btn btn-warning mx-2 my-2">Speak</button>
    </div>
    <div className='container my-3'>
      <h3>Text summary below.</h3>
      <p>
        {text.split(" ").length} words and {text.length} characters.

      </p>
      <p>{0.008 * text.split(" ").length } Time required to read the text.</p>
      <h3>Preview</h3>
      <p>{text}</p>
    </div>
    </>
  )
}
