import React , { useState} from 'react'

export default function TextForm(props) {
  const handleUPClick= () => {
    console.log("Uppercase was clicked"+text);
    const newt = text.toUpperCase();
    setText(newt);

  }
  const handleOnChange= (event) => {
    console.log("on change");
    setText(event.target.value)
  }

  const [text, setText] = useState("this is inside  tha text area");
  return (
    <>
    <div className="container">
      <h2>{props.title}</h2>
        <div className="mb-3">

        <textarea className="form-control" value={text} onChange={handleOnChange} id="exampleFormControlTextarea1" rows="6"></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleUPClick}>Convert to UpperCase</button>

    </div>
    <div className='container my-3'>
      <h3>Text summary below.</h3>
      <p>
        {text.split(" ").length} words and {text.length} characters.

      </p>
      <p>{0.008 *text.split(" ").length } Time required to read the text.</p>
      <h3>Preview</h3>
      <p>{text}</p>
    </div>
    </>
  )
}
